import React from 'react'
import { useState } from 'react'
import { GoTrash } from "react-icons/go";
const Cond =()=> {
   const [tems,setTems]=useState(
    [
    {
      id:1,
      checked : false,
      item : "Read"
    },
    {
      id:2,
      checked : false,
      item : "play chess"
    },
    {
      id:3,
      checked : false,
      item : "run"
    }] )
    const addItem=(item)=>{
      const id=item.length ?tems[tems.length-1].id+1:1
         const addNewitem ={id,checked:"false",item}
    }
    function check(id){
      const litem =tems.map((item)=>item.id===id?{...item,checked:!item.checked}:item)
      setTems(litem)
      localStorage.setItem("dolist",JSON.stringify(litem))
    }
   const trash=(id)=>{
    const litem=tems.filter((item)=>item.id!==id)
    setTems(litem)
    localStorage.setItem("dolist",JSON.stringify(litem))
   }
  return (
   <main>
    {(tems.length)?(
    <ul>
             {
             tems.map((item)=>(
               <li className='item' key={item.id}>
                <input
                type="checkbox"
                onChange={()=>check(item.id)}
                checked ={item.checked}
                  />
                  <label 
                  style={(item.checked)?{textDecoration:'line-through',color:'red'}:null}
                  onDoubleClick={()=>check(item.id)}>{item.item}</label>
                  <GoTrash 
                  role='button'
                  tabIndex={'0'}
                  onClick={()=>trash(item.id)}
                  />

               </li>
             ))}
    </ul>):(<p 
    style={{marginTop:'2rem',
      color:'red'
    }}>List ethuvum podala</p>)}
   </main>
  )
}

export default Cond
